mapper.select:
select A.brandNum, A.brandName,A.companyName, A.managerPhone, B.orderNum from (select brandNum, brandName,companyName, managerPhone from brand where brandNum in ( select brandNum from cart
where
orderNum in (select C.orderNum from ((select * from orders where username=#{username} and
orderDay BETWEEN DATE_ADD(NOW(),INTERVAL -
3
#{before}
MONTH ) AND NOW()
) O inner join (select * from cart where username=#{username} and
validity > 1
validity=#{status}
) C on C.orderNum = O.orderNum) group by C.orderNum) group by brandNum) ) A left join (select O.orderNum, C.brandNum from ((select * from orders where username=#{username} and orderDay BETWEEN DATE_ADD(NOW(),INTERVAL -
3
#{before}
MONTH ) AND NOW()
) O inner join (select * from cart where username=#{username} and
validity > 1
validity=#{status}
) C on C.orderNum = O.orderNum) ) B on A.brandNum =B.brandNum group by orderNum, brandNum

select orderNum, orderDay from orders
where
orderNum in
(select C.orderNum from ((select * from orders where username=#{username} and
orderDay BETWEEN DATE_ADD(NOW(),INTERVAL -
3
#{before}
MONTH ) AND NOW()
) O inner join (select * from cart where username=#{username} and
validity > 1
validity=#{status}
) C on C.orderNum = O.orderNum) group by C.orderNum) order by orderNum desc

    		select O.*, C.*, P.*, S.* from
    			(select * from orders 
    			where orderNum=#{orderNum}
    			)O
    			inner join 
						(select * from cart 
			    		where orderNum=#{orderNum} 
			    		) C
			    		on C.orderNum = O.orderNum
			    		left join (select productPic, productName, productNum, shippingFee from product) P 
			            on P.productNum = C.productNum
			            left join (select shipName, shipPhone, shipAddress, shipDetailAddress, postcode, shipNum from shipping) S
			            on S.shipNum = O.shipNum

select C.*, O.*, P.*, C.carrierId, C.trackId from (select * from orders where username=#{username} and
orderDay BETWEEN DATE_ADD(NOW(),INTERVAL -
3
#{before}
MONTH ) AND NOW()
) O inner join (select * from cart where username=#{username} and
validity > 1
validity=#{status}
) C on C.orderNum = O.orderNum left join (select productPic, productName, productNum, shippingFee from product) P on P.productNum = C.productNum order by O.orderNum desc

mapper.update:
update orders set  
shipNum=#{shipNum}, shippingMemo=#{shippingMemo}, paymentType=#{paymentType}
where orderNum=#{orderNum}

update orders set  
name=#{name},email=#{email}, phone=#{phone}
where orderNum=#{orderNum}
